int ev3_servo_init( void )
{
	char list[ 256 ];
	char *p;
	uint32_t sn;
	uint8_t type_inx;
	int cnt = 0;

	memset( ev3_servo, 0, sizeof( ev3_servo ));

	if ( !ev3_listdir( "/sys/class/servo-motor", list, sizeof( list ))) return ( -1 );

	p = strtok( list, " " );
	while ( p ) {
		if (( ev3_string_suffix( "motor", &p, &sn ) == 1 ) && ( sn < SERVO_DESC__LIMIT_)) {
			get_servo_address( sn, &ev3_servo[ sn ].addr );
			type_inx = get_servo_type_inx( sn );
			ev3_servo[ sn ].type_inx = type_inx;
			ev3_servo[ sn ].port = get_servo_port_inx( sn, PORT_TYPE__NONE_, &ev3_servo[ sn ].extport );
			++cnt;
		}
		p = strtok( NULL, " " );
	}
	return ( cnt );
}

